#思路2 找到每个独立的子串，然后看INDEX是否能连接起来; 进一步的优化，如果PTR2执行到尾部 左括号还是大于0，则直接结束，长度取PTR1 + LEN_LEFT - PTR2

class Solution:
    def longestValidParentheses(self, s: str) -> int:
        totalIns = 0
        maxLen = 0

        linkedList = []

        ptr1 = 0
        while True:
            if ptr1 >= len(s) - 1:
                break

            if s[ptr1] == ')':
                ptr1 += 1
                continue

            totalIns = 1
            ptr2 = ptr1 + 1

            while True:
                if ptr2 >= len(s):
                    ptr1 += 1
                    break

                if s[ptr2] == '(':
                    totalIns += 1
                elif s[ptr2] == ')':
                    totalIns -= 1
                
                if totalIns == 0:
                    #merge and link
                    if len(linkedList) == 0:
                        linkedList.append(ptr1)
                        linkedList.append(ptr2)
                    else:
                        if ptr1 - linkedList[-1] == 1:
                            linkedList.pop(-1)
                            linkedList.append(ptr2)
                        else:
                            linkedList.append(ptr1)
                            linkedList.append(ptr2)
                    ptr1 = ptr2 + 1
                    break
                else:
                    if totalIns > len(s) - ptr2:
                        if ptr1 + totalIns - 1 < ptr2:
                            linkedList.append(ptr1 + totalIns - 1)
                            linkedList.append(ptr2)

                        ptr1 += totalIns
                        ptr2 = len(s)

                        break
                
                ptr2 += 1
                    
        
        print(linkedList)
        if len(linkedList) == 0:
            return 0

        #查找下标能连接起来的最长长度
        for index in range(len(linkedList)):
            if index % 2 == 1:
                if linkedList[index] - linkedList[index-1] + 1 > maxLen:
                    maxLen = linkedList[index] - linkedList[index-1] + 1
     

        return maxLen



solution = Solution()

